import React, { useState, useEffect, useRef } from 'react';
import '../Css/Skill.css';

const Skill = () => {
  const [progress, setProgress] = useState({});
  const skillRef = useRef(null);
  let intervalIds = useRef([]);

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          animateSkillProgress();
          observer.unobserve(entry.target);
        }
      });
    });

    if (skillRef.current) {
      observer.observe(skillRef.current);
    }

    return () => {
      observer.disconnect();
      intervalIds.current.forEach((intervalId) => clearInterval(intervalId));
    };
  }, []);

  const animateSkillProgress = () => {
    intervalIds.current = [];

    skills.forEach((skill, index) => {
      const { stop, id } = skill;

      const intervalId = setInterval(() => {
        setProgress((prevProgress) => {
          if (!prevProgress[id]) {
            return { ...prevProgress, [id]: 0 };
          }

          if (prevProgress[id] < stop) {
            return { ...prevProgress, [id]: prevProgress[id] + 1 };
          } else {
            clearInterval(intervalId);
            return prevProgress;
          }
        });
      }, 100);

      intervalIds.current.push(intervalId);
    });
  };

  const skills = [
    { name: 'Java', stop: 46, id: 'java' },
    { name: 'Php', stop: 80, id: 'php' },
    { name: 'React', stop: 60, id: 'react' },
    // Add more skills here with their respective stop values and IDs
  ];

  return (
    <div className='container' id='skill' ref={skillRef}>
      <div className='row'>
        <div className='col'>
          <div className='service-top'>
            <h1 className='section-title'>
              Ski<span>ll</span>s
            </h1>
          </div>
        </div>
      </div>
      <div className='row'>
        {skills.map((skill, index) => {
          const { name, id } = skill;
          return (
            <div className='col-3 my-5' key={index}>
              <div id='countdown'>
                <svg viewBox='0 0 40 40'>
                  <g
                    transform='translate(20 20) rotate(-90)'
                    strokeWidth='4'
                    fill='none'
                    strokeLinecap='round'
                  >
                    <circle id='c3' r='18' />
                    <circle id='c2' r='18' />
                    <circle id='c1' r='2' cx='18' />
                  </g>
                  <text x='20' y='20'>{progress[id] || 0}%</text>
                </svg>
                <div className='text-center'>
                  <h1>{name}</h1>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Skill;
