import React, { useRef } from 'react';
import '../Css/Home.css';
import User from '../Images/user.jpg';
import CV from '../CV/Ruwal K Shah.pdf';
const Home = () => {

const handleDownload = async() => {
    try {
        const response = await fetch('../CV/Ruwal.docx');
        console.log('Fetch response:', response);
        
        const blob = await response.blob();
        console.log('Blob:', blob);
        
        // Rest of the code
      } catch (error) {
        console.error('Failed to download CV:', error);
    }
}
  
  return (
    <>
        <section id="hero">
            <div className="hero hero-container d-flex justify-content-start align-items-center">
            <div>
                <h1>Hello, <span></span></h1>
                <h1>My Name is <span></span></h1>
                <h1>Ruwal <span></span></h1>
                <button type="button" className="cta" onClick={handleDownload}>Download CV</button>
            </div>
            </div>
        </section>
    </>
  );
};

export default Home;
