import React, { useState, useEffect, useRef } from 'react';
import '../Css/Skill.css';

const Skill = () => {
  const [progress, setProgress] = useState(0);
  const skillRef = useRef(null);
  let intervalId = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          animateSkillProgress();
          observer.unobserve(entry.target);
        }
      });
    });

    if (skillRef.current) {
      observer.observe(skillRef.current);
    }

    return () => {
      observer.disconnect();
      clearInterval(intervalId.current);
    };
  }, []);

  const animateSkillProgress = () => {
    intervalId.current = setInterval(() => {
      setProgress((prevProgress) => {
        if (prevProgress < 46) {
          return prevProgress + 1;
        } else {
          clearInterval(intervalId.current);
          return prevProgress;
        }
      });
    }, 100);
  };

  const skills = ['Java', 'Php', 'React', 'jQuery', 'Bootstrap5', 'mySql', 'HTML', 'Css'];

  return (
    <div className='container' id="skill" ref={skillRef}>
      <div className='row'>
        <div className='col'>
          <div className="service-top">
            <h1 className="section-title">Ski<span>ll</span>s</h1>
          </div>
        </div>
      </div>
      <div className='row'>
        {skills.map((oldval, index) => {
          return (
            <div className='col-3 my-5' key={index}>
              <div id="countdown">
                <svg viewBox="0 0 40 40">
                  <g transform="translate(20 20) rotate(-90)" strokeWidth="4" fill="none" strokeLinecap="round">
                    <circle id="c3" r="18" />
                    <circle id="c2" r="18" />
                    <circle id="c1" r="2" cx="18" />
                  </g>
                  <text x="20" y="20">{progress}%</text>
                </svg>
                <div className='text-center'>
                  <h1>{oldval}</h1>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Skill;
