import React, { useState, useEffect } from 'react';
import '../Css/Skill.css';

const Skill = () => {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const incrementProgress = () => {
      if (progress < 46) {
        setProgress(progress + 1);
      }
    };

    const timer = setTimeout(incrementProgress, 100);

    return () => clearTimeout(timer);
  }, [progress]);

  return (
    <div className='container'>
      <div className='row'>
        <div className='col-4'>
          <div id="countdown">
            <svg viewBox="0 0 40 40">
              <g transform="translate(20 20) rotate(-90)" strokeWidth="4" fill="none" strokeLinecap="round">
                <circle id="c3" r="18" />
                <circle id="c2" r="18" />
                <circle id="c1" r="2" cx="18" />
              </g>
              <text x="20" y="20">{progress}%</text>
            </svg>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Skill;
