import React from 'react'
import '../Css/Service.css';
import ServiceList from './ServiceList';
import SoftwareImg from '../Images/software.png';
import WebDevelopment from '../Images/web-development.png';
import Ui from '../Images/ui.png';
const Service = () => {
    const service_text = [
        {
            img : SoftwareImg,
            title : 'Software Development',
            text : 'We offer comprehensive software development services tailored to meet your specific business needs. Our team of experienced software developers utilizes cutting-edge technologies and best practices to create high-quality software solutions. From conceptualization to deployment, we handle every phase of the software development life cycle with precision and attention to detail.\n\nOur software development process involves thorough requirements gathering, system design, coding, testing, and maintenance. We employ agile methodologies to ensure flexibility and responsiveness throughout the development process.'
        },
        {
            img : WebDevelopment,
            title : 'Web Application Development',
            text : 'Web development involves creating websites and web applications for the internet or intranet. It includes front-end development, back-end development, and database management.'
        },
        {
            img : Ui,
            title : 'User Interface Development',
            text : 'User Interface (UI) development focuses on creating visually appealing and user-friendly interfaces for websites and applications. It involves designing and implementing the graphical elements, layouts, and interactive components that users interact with. UI developers work closely with designers to translate design mockups into functional interfaces using HTML, CSS, and JavaScript. They ensure that the UI is responsive, accessible, and optimized for a seamless user experience across different devices and platforms.'
        }
    ]
  return (
    <div className='container'>
        <div className='row'>
            <div className='col'>
                <section id="services">
                    <div className="services">
                     <div className='row'>
                        <div className='col'>
                        <div className="service-top">
                            <h1 className="section-title">Serv<span>i</span>ces</h1>
                        </div>
                        </div>
                     </div>
                    <div className='row'>
                        {service_text.map( (index,i) => {
                          return <ServiceList key = {i} imgSrc = {index.img} title = {index.title} text = {index.text}></ServiceList>
                        })}
                    </div>
                </div>
            </section>
            </div>
        </div>
    </div>
  )
}

export default Service
